@patch
def _repr_markdown_(self:ChatCompletion):
    det = '\n- '.join(f'{k}: {v}' for k,v in dict(self).items())
    res = self.choices
    if not res: return f"- {det}"
    return f"""{res[0].message.content}

<details>

- {det}

</details>"""

